// src/components/Timer.tsx

import React, { useState, useEffect } from 'react';

type Props = {
  workDuration: number;
  breakDuration: number;
};

const Timer: React.FC<Props> = ({ workDuration, breakDuration }) => {
  const [timeLeft, setTimeLeft] = useState(workDuration * 60);
  const [isRunning, setIsRunning] = useState(false);
  const [isWorking, setIsWorking] = useState(true);

  const playSound = (soundUrl: string) => {
    const audio = new Audio(soundUrl);
    audio.play().catch((error) => {
      console.error("Error playing audio:", error);
    });
  };

  useEffect(() => {
    if (timeLeft === 0) {
      playSound('https://drive.google.com/file/d/1_pkXO-rsWBz7w9b1JCfaJWSFSJPoOR6U/view?usp=drive_link');
      setIsWorking(!isWorking);
      setTimeLeft(isWorking ? breakDuration * 60 : workDuration * 60);
    }

    let interval: NodeJS.Timeout;

    if (isRunning) {
      interval = setInterval(() => {
        setTimeLeft(timeLeft - 1);
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [timeLeft, isRunning, isWorking, workDuration, breakDuration]);

  return (
    <div>
      <h1>{isWorking ? 'Work' : 'Break'}</h1>
      <p>{Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, '0')}</p>
      <button onClick={() => setIsRunning(!isRunning)}>
        {isRunning ? 'Pause' : 'Start'}
      </button>
      <button onClick={() => setTimeLeft(isWorking ? workDuration * 60 : breakDuration * 60)}>Reset</button>
    </div>
  );
};

export default Timer;
