// src/components/Sound.tsx

import React, { useState } from 'react';

const Sound: React.FC = () => {
  const [isSoundEnabled, setIsSoundEnabled] = useState(true); // A state to track sound settings

  const toggleSound = () => {
    setIsSoundEnabled(!isSoundEnabled);
  };

  return (
    <div>
      <h2>Sound Settings</h2>
      <div>
        <label>
          <input
            type="checkbox"
            checked={isSoundEnabled}
            onChange={toggleSound}
          />
          Enable Sound Notifications
        </label>
      </div>
      {isSoundEnabled && (
        <div>
          {/* Add options for selecting a sound file or customizing sound settings */}
          {/* Example: You can provide an input for selecting a sound file */}
          <label htmlFor="soundFile">Select a sound file:</label>
          <input type="file" id="soundFile" accept="audio/*" />
        </div>
      )}
    </div>
  );
};

export default Sound;
